mapper.insert:
INSERT INTO store_notice (
notice_regdate,
notice_content,
notice_type,
store_no
) VALUES (
NOW(),
#{noticeContent},
#{noticeType},
#{storeNo}
);

mapper.update:
update store_notice
set notice_content =  #{noticeContent}
,notice_regdate = now()
,notice_type = #{noticeType}
,modi = 'Y'
where notice_no = #{noticeNo}

update store_notice
set status = #{status}
where notice_no = #{noticeNo}

mapper.select:
select store_id
from store
where manager_name = #{managerName} and store_business_no = #{storeBusinessNo}

select count(*)
from store
where store_id = #{storeId} and store_business_no = #{storeBusinessNo}

mapper.update:
update store
set store_pw = #{newPw}
where store_id = #{storeId} and store_business_no = #{storeBusinessNo}

mapper.delete:
delete from store_sns_link
where store_no = #{storeNo}

    and sns_link not in

         #{snsLink.snsLink}

mapper.select:
select sns_link
from store_sns_link
where store_no = #{storeNo}

mapper.delete:
DELETE FROM store_img
WHERE store_no = #{storeNo}

    AND store_img_location NOT IN

        #{img.storeImgLocation}

mapper.select:
SELECT store_img_location
FROM store_img
WHERE store_no = #{storeNo}

select day_off_start, day_off_end, set_no
from day_off_set
where store_no = #{storeNo}

mapper.update:

update day_off_day
set day_off_fix_status = 'N'
where store_no = #{storeNo}
and day_off_day NOT IN

    #{dayOffDay.dayOffDay}
;



    update day_off_day
    set day_off_fix_status = 'Y'
    where store_no = #{storeNo}
    and day_off_day = #{dayOffDay.dayOffDay};

mapper.select:
select count(*)
from user_likes
where store_no = #{storeNo}

select count(*)
from reservation reserve
where reservation_status = '완료' and store_no = #{storeNo}

select count(*)
from payment pay
join reservation reserve on reserve.reservation_no = pay.reservation_no
where reserve.store_no = #{storeNo}
and reserve.reservation_status = '확정'
and pay.payment_status='결제완료';

SELECT TO_CHAR(date(reg_time), 'MM-DD') AS date, COUNT(*) AS count
FROM reservation
WHERE store_no = #{storeNo}
AND EXTRACT(YEAR FROM reg_time) = #{year}
AND EXTRACT(MONTH FROM reg_time) = #{month}
GROUP BY date(reg_time)
ORDER BY date(reg_time)

SELECT TO_CHAR(date(reg_time), 'YYYY-MM') AS month, COUNT(*) AS count
FROM reservation
WHERE store_no = #{storeNo}
AND EXTRACT(YEAR FROM reg_time) = #{year}
GROUP BY month
ORDER BY month
