mapper.select:
SELECT user_id, user_pw, user_name, user_birth, user_phonenum, user_gender, user_signup, user_img_url, login_type
FROM USERS
WHERE user_id = #{userId}

mapper.update:
UPDATE USERS
SET
user_name = #{userName},
user_phonenum = #{userPhonenum},
user_birth = #{userBirth},
user_gender = #{userGender},
user_img_url = #{userImgUrl}

    , user_pw = #{userPw}

WHERE user_id = #{userId}

mapper.select:
SELECT user_id
FROM USERS
WHERE user_name = #{userName}
AND user_phonenum = #{phonenum}
AND login_type = 'GENERAL'
LIMIT 1

SELECT COUNT(*)
FROM USERS
WHERE user_id = #{userId}
AND user_name = #{userName}
AND user_phonenum = #{phonenum}
AND login_type = 'GENERAL'

mapper.update:
UPDATE USERS
SET user_pw = #{newPassword}
WHERE user_id = #{userId}
